// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

// LayerZero OApp imports
import {OApp, Origin, MessagingFee, MessagingReceipt} from "@layerzerolabs/oapp-evm/contracts/oapp/OApp.sol";
import {OAppReceiver} from "@layerzerolabs/oapp-evm/contracts/oapp/OAppReceiver.sol";

// OpenZeppelin imports
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title CrosschainStrategyAdapter
 * @author Elitra
 * @notice Simple adapter for cross-chain messaging via LayerZero
 * @dev Only the registered vault can send messages. This is a sender-only adapter.
 */
contract CrosschainStrategyAdapter is OApp {
    using SafeERC20 for IERC20;

    // ========================================= STATE =========================================

    /// @notice The vault address that is allowed to call sendStrategy
    address public vault;

    // ========================================= ERRORS =========================================

    error OnlyVault();
    error InvalidVault();
    error InvalidRecipient();
    error InvalidFee();

    // ========================================= EVENTS =========================================

    /// @notice Emitted when a message is sent to another chain
    /// @param dstEid The destination endpoint ID
    /// @param recipient The recipient address on the destination chain
    event MessageSent(uint32 indexed dstEid, address indexed recipient);

    /// @notice Emitted when the vault address is updated
    /// @param oldVault The previous vault address
    /// @param newVault The new vault address
    event VaultUpdated(address indexed oldVault, address indexed newVault);

    /// @notice Emitted when tokens are recovered via emergency function
    /// @param token The token address recovered
    /// @param to The recipient address
    /// @param amount The amount recovered
    event EmergencyRecovery(address indexed token, address indexed to, uint256 amount);

    // ========================================= MODIFIERS =========================================

    modifier onlyVault() {
        if (msg.sender != vault) revert OnlyVault();
        _;
    }

    // ========================================= CONSTRUCTOR =========================================

    /**
     * @notice Constructor
     * @param _endpoint LayerZero endpoint address
     * @param _owner Contract owner
     * @param _vault The vault address allowed to send messages
     */
    constructor(address _endpoint, address _owner, address _vault) OApp(_endpoint, _owner) Ownable(_owner) {
        if (_vault == address(0)) revert InvalidVault();
        vault = _vault;
    }

    // ========================================= CORE FUNCTIONS =========================================

    /**
     * @notice Send a message to another chain
     * @param _dstEid Destination endpoint ID
     * @param _recipient Address of the recipient on the destination chain
     * @param _options LayerZero message options
     * @return receipt The messaging receipt
     */
    function sendStrategy(
        uint32 _dstEid,
        address _recipient,
        bytes calldata _options
    ) external payable onlyVault returns (MessagingReceipt memory receipt) {
        bytes memory _payload = abi.encode(_recipient);
        MessagingFee memory _fee = _quote(_dstEid, _payload, _options, false);

        if (msg.value < _fee.nativeFee) revert InvalidFee();

        receipt = _lzSend(_dstEid, _payload, _options, _fee, payable(msg.sender));

        emit MessageSent(_dstEid, _recipient);
    }

    /**
     * @notice Quote the fee for sending a message
     * @param _dstEid Destination endpoint ID
     * @param _recipient Address of the recipient on the destination chain
     * @param _options LayerZero message options
     * @param _payInLzToken Whether to pay in LZ token
     * @return fee The messaging fee
     */
    function quoteStrategy(
        uint32 _dstEid,
        address _recipient,
        bytes calldata _options,
        bool _payInLzToken
    ) external view returns (MessagingFee memory fee) {
        bytes memory _payload = abi.encode(_recipient);
        return _quote(_dstEid, _payload, _options, _payInLzToken);
    }

    // ========================================= ADMIN FUNCTIONS =========================================

    /**
     * @notice Update the vault address
     * @param _newVault The new vault address
     */
    function setVault(address _newVault) external onlyOwner {
        if (_newVault == address(0)) revert InvalidVault();
        address oldVault = vault;
        vault = _newVault;
        emit VaultUpdated(oldVault, _newVault);
    }

    /**
     * @notice Recover tokens or ETH accidentally sent to the contract
     * @param token The token address to recover (address(0) for ETH)
     * @param to The recipient address
     * @param amount The amount to recover
     */
    function emergencyRecover(address token, address to, uint256 amount) external onlyOwner {
        if (to == address(0)) revert InvalidRecipient();

        if (token == address(0)) {
            payable(to).transfer(amount);
        } else {
            IERC20(token).safeTransfer(to, amount);
        }

        emit EmergencyRecovery(token, to, amount);
    }

    // ========================================= INTERNAL FUNCTIONS =========================================

    /**
     * @notice Internal function to handle LayerZero receive
     * @dev This adapter is a sender only, so receive is disabled
     * @inheritdoc OAppReceiver
     */
    function _lzReceive(
        Origin calldata,
        bytes32,
        bytes calldata,
        address,
        bytes calldata
    ) internal pure override {
        revert("Sender only");
    }

    /**
     * @notice Allow receiving ETH
     */
    receive() external payable {}
}

