// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import { IRedemptionHook, RedemptionMode } from "../interfaces/IRedemptionHook.sol";
import { IElitraVault } from "../interfaces/IElitraVault.sol";

/// @title HybridRedemptionHook
/// @notice Redemption hook: instant if liquidity available, else queue
contract HybridRedemptionHook is IRedemptionHook {
    event RedemptionProcessed(
        address indexed vault,
        address indexed receiver,
        RedemptionMode mode,
        uint256 assets
    );

    /// @inheritdoc IRedemptionHook
    function beforeRedeem(
        IElitraVault vault,
        uint256, /* shares */
        uint256 assets,
        address, /* owner */
        address receiver
    ) external returns (RedemptionMode mode, uint256 actualAssets) {
        uint256 availableBalance = vault.getAvailableBalance();

        if (availableBalance >= assets) {
            emit RedemptionProcessed(address(vault), receiver, RedemptionMode.INSTANT, assets);
            return (RedemptionMode.INSTANT, assets);
        } else {
            emit RedemptionProcessed(address(vault), receiver, RedemptionMode.QUEUED, assets);
            return (RedemptionMode.QUEUED, assets);
        }
    }

    /// @inheritdoc IRedemptionHook
    function afterRedeem(
        address, /* receiver */
        uint256, /* shares */
        uint256, /* assets */
        bool /* instant */
    ) external {
        // Empty for now - reserved for future extensibility
        // Could be used for: analytics, notifications, rewards distribution, etc.
    }
}

src/hooks/ManualBalanceUpdateHook.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import { Errors } from "../libraries/Errors.sol";
import { IBalanceUpdateHook } from "../interfaces/IBalanceUpdateHook.sol";
import { Auth, Authority } from "@solmate/auth/Auth.sol";
import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";

/// @title ManualBalanceUpdateHook
/// @notice Hook that validates balance updates based on price change thresholds
/// @dev Pure calculation helper - does not modify vault state
contract ManualBalanceUpdateHook is IBalanceUpdateHook, Auth {
    using Math for uint256;

    uint256 public maxPercentageChange;
    uint256 constant DENOMINATOR = 1e18;
    uint256 constant MAX_PERCENTAGE_THRESHOLD = 1e17; // 10%

    event MaxPercentageUpdated(uint256 oldMax, uint256 newMax);

    constructor(address _owner) Auth(_owner, Authority(address(0))) {
        maxPercentageChange = 1e16; // 1% default
    }

    /// @inheritdoc IBalanceUpdateHook
    function beforeBalanceUpdate(
        uint256 currentPPS,
        uint256 totalSupply,
        uint256 idleAssets,
        uint256 newAggregatedBalance
    ) external view returns (bool shouldContinue, uint256 newPPS) {
        // Calculate total assets
        uint256 totalAssets = idleAssets + newAggregatedBalance;

        // Calculate new price per share
        if (totalSupply == 0) {
            newPPS = DENOMINATOR;
            shouldContinue = true;
        } else {
            newPPS = totalAssets.mulDiv(DENOMINATOR, totalSupply, Math.Rounding.Floor);

            // Check if price change exceeds threshold
            uint256 percentageChange = _calculatePercentageChange(currentPPS, newPPS);
            shouldContinue = percentageChange <= maxPercentageChange;
        }
    }

    /// @inheritdoc IBalanceUpdateHook
    function afterBalanceUpdate(
        uint256, /* oldBalance */
        uint256, /* newBalance */
        uint256 /* newPPS */
    ) external view {
        // Empty for now - reserved for future extensibility
        // Could be used for: logging, notifications, external integrations, etc.
    }

    /// @inheritdoc IBalanceUpdateHook
    function updateMaxPercentageChange(uint256 newThreshold) external requiresAuth {
        require(newThreshold < MAX_PERCENTAGE_THRESHOLD, Errors.InvalidMaxPercentage());
        emit MaxPercentageUpdated(maxPercentageChange, newThreshold);
        maxPercentageChange = newThreshold;
    }

    /// @dev Calculate percentage change between two prices
    function _calculatePercentageChange(uint256 oldPrice, uint256 newPrice) private pure returns (uint256) {
        if (oldPrice == 0) return 0;
        uint256 diff = newPrice > oldPrice ? newPrice - oldPrice : oldPrice - newPrice;
        return diff.mulDiv(DENOMINATOR, oldPrice, Math.Rounding.Ceil);
    }
}

src/interfaces/IBalanceUpdateHook.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

/// @title IBalanceUpdateHook
/// @notice Hook interface for validating and processing balance updates
interface IBalanceUpdateHook {
    /// @notice Called before balance update to validate and calculate new PPS
    /// @param currentPPS Current price per share
    /// @param totalSupply Total vault shares
    /// @param idleAssets Idle assets in vault
    /// @param newAggregatedBalance New aggregated balance from strategies
    /// @return shouldContinue If false, update should be rejected and vault paused
    /// @return newPPS Calculated new price per share
    function beforeBalanceUpdate(
        uint256 currentPPS,
        uint256 totalSupply,
        uint256 idleAssets,
        uint256 newAggregatedBalance
    ) external view returns (bool shouldContinue, uint256 newPPS);

    /// @notice Called after balance update completes (for future extensibility)
    /// @param oldBalance Previous aggregated balance
    /// @param newBalance New aggregated balance
    /// @param newPPS New price per share
    function afterBalanceUpdate(
        uint256 oldBalance,
        uint256 newBalance,
        uint256 newPPS
    ) external view;

    /// @notice Update the maximum price change threshold
    /// @param newThreshold New max percentage change (1e18 = 100%)
    function updateMaxPercentageChange(uint256 newThreshold) external;

    /// @notice Get current max percentage change threshold
    function maxPercentageChange() external view returns (uint256);
}

src/interfaces/IElitraVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import { IERC4626 } from "@openzeppelin/contracts/interfaces/IERC4626.sol";
import { IBalanceUpdateHook } from "./IBalanceUpdateHook.sol";
import { IRedemptionHook } from "./IRedemptionHook.sol";
import { IVaultBase } from "./IVaultBase.sol";

/// @notice Call structure for batch operations
struct Call {
    address target;
    bytes data;
    uint256 value;
}

/// @title IElitraVault
/// @notice Interface for ElitraVault with adapter integration
interface IElitraVault is IERC4626, IVaultBase {
    /// @notice Pending redemption data structure
    struct PendingRedeem {
        uint256 shares;
        uint256 assets;
    }

    // Events
    event UnderlyingBalanceUpdated(uint256 indexed timestamp, uint256 oldBalance, uint256 newBalance);
    event PPSUpdated(uint256 indexed timestamp, uint256 oldPPS, uint256 newPPS);
    event VaultPausedDueToThreshold(uint256 indexed timestamp, uint256 oldPPS, uint256 newPPS);
    event BalanceUpdateHookUpdated(address indexed oldHook, address indexed newHook);
    event RedemptionHookUpdated(address indexed oldHook, address indexed newHook);
    event RedeemRequest(
        address indexed receiver,
        address indexed owner,
        uint256 assets,
        uint256 shares,
        bool instant
    );
    event RequestFulfilled(address indexed receiver, uint256 shares, uint256 assets);
    event RequestCancelled(address indexed receiver, uint256 shares, uint256 assets);

    // Balance update hook integration
    function updateBalance(uint256 newAggregatedBalance) external;
    function setBalanceUpdateHook(IBalanceUpdateHook hook) external;
    function balanceUpdateHook() external view returns (IBalanceUpdateHook);
    function lastBlockUpdated() external view returns (uint256);
    function lastPricePerShare() external view returns (uint256);
    function aggregatedUnderlyingBalances() external view returns (uint256);

    // Redemption hook integration
    function setRedemptionHook(IRedemptionHook hook) external;
    function redemptionHook() external view returns (IRedemptionHook);
    function getAvailableBalance() external view returns (uint256);
    function requestRedeem(uint256 shares, address receiver, address owner) external returns (uint256);
    function fulfillRedeem(address receiver, uint256 shares, uint256 assets) external;
    function cancelRedeem(address receiver, uint256 shares, uint256 assets) external;
    function pendingRedeemRequest(address user) external view returns (uint256 assets, uint256 pendingShares);
}

src/interfaces/IRedemptionHook.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import { IElitraVault } from "./IElitraVault.sol";

/// @notice Redemption mode returned by hook
enum RedemptionMode {
    INSTANT,   // Withdraw immediately from idle balance
    QUEUED     // Add to pending redemption queue
}

/// @title IRedemptionHook
/// @notice Hook interface for determining redemption mode and processing
interface IRedemptionHook {
    /// @notice Called before redemption to determine mode and validate
    /// @param vault The vault processing the redemption
    /// @param shares Amount of shares to redeem
    /// @param assets Equivalent assets for those shares
    /// @param owner Owner of the shares
    /// @param receiver Receiver of assets
    /// @return mode INSTANT or QUEUED
    /// @return actualAssets Assets to withdraw (may differ due to strategy rules)
    function beforeRedeem(
        IElitraVault vault,
        uint256 shares,
        uint256 assets,
        address owner,
        address receiver
    ) external returns (RedemptionMode mode, uint256 actualAssets);

    /// @notice Called after redemption completes (for future extensibility)
    /// @param receiver Receiver of assets
    /// @param shares Amount of shares redeemed
    /// @param assets Amount of assets withdrawn
    /// @param instant Whether it was instant or queued
    function afterRedeem(
        address receiver,
        uint256 shares,
        uint256 assets,
        bool instant
    ) external;
}

