// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import { Call } from "./IElitraVault.sol";
/**
 * @title IMultichainDepositAdapter
 * @notice Bridge adapter, for handling crosschain fund movements
 *
 */
interface IBridgeAdapter {
    // ========================================= STRUCTS =========================================

    /**
     * @notice Single call operation for batch execution
     * @param target Contract to call
     * @param value Native value to send
     * @param data Calldata for the call
     */

    /**
     * @notice Deposit status tracking
     */
    enum DepositStatus {
        Pending,        // Recorded, being processed
        Success,        // Successfully deposited to vault
        ZapFailed,      // Zap execution failed
        DepositFailed,  // Vault deposit failed
        RefundSent,     // Refunded to source chain
        RefundFailed    // Refund failed, needs manual intervention
    }

    /**
     * @notice Record of a cross-chain deposit attempt
     * @param user Receiver of vault shares
     * @param srcEid Source chain endpoint ID
     * @param tokenIn Token received from bridge
     * @param amountIn Amount received
     * @param vault Target vault
     * @param sharesReceived Vault shares minted (0 if failed)
     * @param timestamp Deposit time
     * @param status Current status
     * @param guid LayerZero message GUID
     * @param failureReason Error data if failed
     */
    struct DepositRecord {
        address user;
        uint32 srcEid;
        address tokenIn;
        uint256 amountIn;
        address vault;
        uint256 sharesReceived;
        uint256 timestamp;
        DepositStatus status;
        bytes32 guid;
        bytes failureReason;
    }

    // ========================================= EVENTS =========================================

    event DepositRecorded(
        uint256 indexed depositId,
        address indexed user,
        address indexed vault,
        uint256 amountIn,
        uint32 srcEid
    );

    event DepositSuccess(
        uint256 indexed depositId,
        address indexed user,
        address indexed vault,
        uint256 sharesReceived
    );

    event DepositFailed(
        uint256 indexed depositId,
        address indexed user,
        bytes reason
    );

    event RefundSent(
        uint256 indexed depositId,
        address indexed user,
        uint256 amount,
        uint32 dstEid
    );

    event ZapExecuted(
        uint256 indexed depositId,
        uint256 numCalls,
        uint256 amountOut
    );

    // ========================================= CORE FUNCTIONS =========================================
    /**
     * @notice Execute batch operations (zapping)
     * @param calls Array of Call structs containing target, data, and value
     * @dev Only callable by this contract during deposit processing
     * @dev Similar to ElitraVault.manageBatch but without auth restrictions
     */
    function manageBatch(
        Call[] calldata calls
    ) external;

    /**
     * @notice Deposit tokens into vault for receiver
     * @param vault Vault address
     * @param receiver Address to receive vault shares
     * @param amount Amount to deposit
     * @return shares Vault shares received
     * @dev Only callable by this contract during deposit processing
     */
    function depositToVault(
        address vault,
        address receiver,
        uint256 amount
    ) external returns (uint256 shares);

    /**
     * @notice Process deposit with zapping (external for try-catch)
     * @param depositId Deposit record ID
     * @param vault Target vault
     * @param receiver Share receiver
     * @param token Token to deposit (after zap)
     * @param amount Amount received
     * @param zapCalls Zap operations to execute
     * @return shares Vault shares received
     * @dev Only callable by this contract
     */
    function processDeposit(
        uint256 depositId,
        address vault,
        address receiver,
        address token,
        uint256 amount,
        Call[] calldata zapCalls
    ) external returns (uint256 shares);

    /**
     * @notice Attempt automatic refund (external for try-catch)
     * @param depositId Deposit record ID
     * @dev Only callable by this contract
     */
    function safeAttemptRefund(uint256 depositId) external;

    /**
     * @notice Manual refund for failed deposits (operator only)
     * @param depositId Deposit record ID
     */
    function manualRefund(uint256 depositId) external;

    /**
     * @notice Batch manual refund
     * @param depositIds Array of deposit IDs to refund
     */
    function batchManualRefund(uint256[] calldata depositIds) external;

    // ========================================= ADMIN FUNCTIONS =========================================

    /**
     * @notice Pause the adapter
     */
    function pause() external;

    /**
     * @notice Unpause the adapter
     */
    function unpause() external;

    /**
     * @notice Deposit ETH for refund gas fees
     */
    function depositRefundGas() external payable;

    /**
     * @notice Emergency recover stuck tokens
     * @param token Token address (address(0) for native)
     * @param to Recipient
     * @param amount Amount to recover
     */
    function emergencyRecover(address token, address to, uint256 amount) external;

    /**
     * @notice Set supported OFT token mapping
     * @param token Token address
     * @param oft OFT contract address
     * @param isActive Whether to activate or deactivate
     */
    function setSupportedOFT(address token, address oft, bool isActive) external;

    /**
     * @notice Set supported vault
     * @param vault Vault address
     * @param isActive Whether to activate or deactivate
     */
    function setSupportedVault(address vault, bool isActive) external;

    // ========================================= VIEW FUNCTIONS =========================================

    /**
     * @notice Get deposit record
     * @param depositId Deposit ID
     * @return Deposit record details
     */
    function getDepositRecord(uint256 depositId) external view returns (DepositRecord memory);

    /**
     * @notice Get user's deposit IDs
     * @param user User address
     * @return Array of deposit IDs
     */
    function getUserDepositIds(address user) external view returns (uint256[] memory);

    /**
     * @notice Get failed deposits needing manual intervention
     * @param startId Start scanning from this ID
     * @param limit Max records to return
     * @return depositIds Array of failed deposit IDs
     */
    function getFailedDeposits(uint256 startId, uint256 limit)
        external
        view
        returns (uint256[] memory depositIds);

    /**
     * @notice Quote refund fee
     * @param depositId Deposit ID
     * @return nativeFee ETH needed for refund
     */
    function quoteRefundFee(uint256 depositId) external view returns (uint256 nativeFee);

    /**
     * @notice Check if vault is supported
     * @param vault Vault address
     * @return Whether vault is supported
     */
    function isVaultSupported(address vault) external view returns (bool);

    /**
     * @notice Total number of deposits
     * @return Total deposit count
     */
    function totalDeposits() external view returns (uint256);
}

src/interfaces/IElitraVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import { IERC4626 } from "@openzeppelin/contracts/interfaces/IERC4626.sol";
import { IBalanceUpdateHook } from "./IBalanceUpdateHook.sol";
import { IRedemptionHook } from "./IRedemptionHook.sol";
import { IVaultBase } from "./IVaultBase.sol";

/// @notice Call structure for batch operations
struct Call {
    address target;
    bytes data;
    uint256 value;
}

/// @title IElitraVault
/// @notice Interface for ElitraVault with adapter integration
interface IElitraVault is IERC4626, IVaultBase {
    /// @notice Pending redemption data structure
    struct PendingRedeem {
        uint256 shares;
        uint256 assets;
    }

    // Events
    event UnderlyingBalanceUpdated(uint256 indexed timestamp, uint256 oldBalance, uint256 newBalance);
    event PPSUpdated(uint256 indexed timestamp, uint256 oldPPS, uint256 newPPS);
    event VaultPausedDueToThreshold(uint256 indexed timestamp, uint256 oldPPS, uint256 newPPS);
    event BalanceUpdateHookUpdated(address indexed oldHook, address indexed newHook);
    event RedemptionHookUpdated(address indexed oldHook, address indexed newHook);
    event RedeemRequest(
        address indexed receiver,
        address indexed owner,
        uint256 assets,
        uint256 shares,
        bool instant
    );
    event RequestFulfilled(address indexed receiver, uint256 shares, uint256 assets);
    event RequestCancelled(address indexed receiver, uint256 shares, uint256 assets);

    // Balance update hook integration
    function updateBalance(uint256 newAggregatedBalance) external;
    function setBalanceUpdateHook(IBalanceUpdateHook hook) external;
    function balanceUpdateHook() external view returns (IBalanceUpdateHook);
    function lastBlockUpdated() external view returns (uint256);
    function lastPricePerShare() external view returns (uint256);
    function aggregatedUnderlyingBalances() external view returns (uint256);

    // Redemption hook integration
    function setRedemptionHook(IRedemptionHook hook) external;
    function redemptionHook() external view returns (IRedemptionHook);
    function getAvailableBalance() external view returns (uint256);
    function requestRedeem(uint256 shares, address receiver, address owner) external returns (uint256);
    function fulfillRedeem(address receiver, uint256 shares, uint256 assets) external;
    function cancelRedeem(address receiver, uint256 shares, uint256 assets) external;
    function pendingRedeemRequest(address user) external view returns (uint256 assets, uint256 pendingShares);
}

src/interfaces/IRedemptionHook.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import { IElitraVault } from "./IElitraVault.sol";

/// @notice Redemption mode returned by hook
enum RedemptionMode {
    INSTANT,   // Withdraw immediately from idle balance
    QUEUED     // Add to pending redemption queue
}

/// @title IRedemptionHook
/// @notice Hook interface for determining redemption mode and processing
interface IRedemptionHook {
    /// @notice Called before redemption to determine mode and validate
    /// @param vault The vault processing the redemption
    /// @param shares Amount of shares to redeem
    /// @param assets Equivalent assets for those shares
    /// @param owner Owner of the shares
    /// @param receiver Receiver of assets
    /// @return mode INSTANT or QUEUED
    /// @return actualAssets Assets to withdraw (may differ due to strategy rules)
    function beforeRedeem(
        IElitraVault vault,
        uint256 shares,
        uint256 assets,
        address owner,
        address receiver
    ) external returns (RedemptionMode mode, uint256 actualAssets);

    /// @notice Called after redemption completes (for future extensibility)
    /// @param receiver Receiver of assets
    /// @param shares Amount of shares redeemed
    /// @param assets Amount of assets withdrawn
    /// @param instant Whether it was instant or queued
    function afterRedeem(
        address receiver,
        uint256 shares,
        uint256 assets,
        bool instant
    ) external;
}

src/interfaces/ITransactionGuard.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

/// @title ITransactionGuard
/// @author Elitra
/// @notice Interface for transaction guards
interface ITransactionGuard {
    /// @notice Checks if a transaction is safe to execute
    /// @param from The caller (e.g. Manager/Bridge)
    /// @param data The calldata being sent to the target
    /// @param value The value being sent
    /// @return safe True if the transaction is safe, false otherwise
    function validate(address from, bytes calldata data, uint256 value) external view returns (bool safe);
}

src/interfaces/IVaultBase.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import { Call } from "./IElitraVault.sol";
import { ITransactionGuard } from "./ITransactionGuard.sol";

/**
 * @title IVaultBase
 * @notice Interface for base vault functionality including guards and management
 */
interface IVaultBase {
    // Events
    event GuardUpdated(address indexed target, address indexed guard);
    event GuardRemoved(address indexed target);
    event ManageBatchOperation(
        uint256 indexed index,
        address indexed target,
        bytes4 functionSig,
        uint256 value,
        bytes result
    );

    // Guard management
    function setGuard(address target, address guard) external;
    function removeGuard(address target) external;
    function guards(address target) external view returns (ITransactionGuard);

    // Strategy management
    function manageBatch(Call[] calldata calls) external;

    // Emergency controls
    function pause() external;
    function unpause() external;
}

